# Example Usage - Quick Start Guide\n\n\"\"\"Example usage of the Grammar Scoring Engine\"\"\"\n\nfrom src.audio_processor import AudioProcessor\nfrom src.text_processor import TextProcessor\nfrom src.grammar_scorer import GrammarScorer\nfrom src.utils import print_results_summary, save_results\n\n# Example 1: Score a single audio file\ndef example_single_file():\n    \"\"\"\n    Score a single audio file\n    \"\"\"\n    audio_path = './data/sample_audio.wav'\n    \n    # Initialize components\n    audio_processor = AudioProcessor()\n    text_processor = TextProcessor()\n    grammar_scorer = GrammarScorer()\n    \n    # Process audio\n    audio, sr = audio_processor.preprocess_audio(audio_path)\n    duration = audio_processor.get_duration(audio, sr)\n    pause_count = audio_processor.get_pause_count(audio, sr)\n    \n    # Convert to text\n    transcript = text_processor.speech_to_text(audio_path)\n    \n    # Preprocess text\n    text_data = text_processor.preprocess_text(transcript)\n    \n    # Score grammar\n    score = grammar_scorer.score_grammar(\n        transcript,\n        duration,\n        pause_count,\n        text_data['pos_tags']\n    )\n    \n    # Prepare result\n    result = {\n        'audio_file': audio_path,\n        'transcript': transcript,\n        'final_score': score['final_score'],\n        'components': score['components'],\n        'errors': score['errors'],\n        'statistics': score['statistics'],\n    }\n    \n    # Display and save\n    print_results_summary(result)\n    save_results(result, './results/sample_results.json')\n    \n    return result\n\n\n# Example 2: Batch processing\ndef example_batch_processing():\n    \"\"\"\n    Process multiple audio files\n    \"\"\"\n    from pathlib import Path\n    \n    audio_dir = './data'\n    results = []\n    \n    # Find all audio files\n    audio_files = list(Path(audio_dir).glob('*.wav'))\n    audio_files += list(Path(audio_dir).glob('*.mp3'))\n    \n    # Process each file\n    for audio_file in audio_files:\n        # ... same processing as example_single_file() ...\n        pass\n    \n    return results\n\n\n# Example 3: Custom configuration\ndef example_custom_config():\n    \"\"\"\n    Use custom configuration\n    \"\"\"\n    from src.config import AudioProcessor, AUDIO_CONFIG\n    \n    # Modify config\n    AUDIO_CONFIG['sample_rate'] = 44100\n    AUDIO_CONFIG['normalize'] = True\n    \n    # Initialize with custom config\n    audio_processor = AudioProcessor(sample_rate=44100)\n    \n    # ... process audio ...\n\n\nif __name__ == '__main__':\n    print(\"Grammar Scoring Engine - Example Usage\")\n    print(\"=\"*60)\n    print(\"\\n1. Single File Processing:\")\n    print(\"   from examples import example_single_file\")\n    print(\"   result = example_single_file()\")\n    print(\"\\n2. Batch Processing:\")\n    print(\"   from examples import example_batch_processing\")\n    print(\"   results = example_batch_processing()\")\n    print(\"\\n3. Custom Configuration:\")\n    print(\"   from examples import example_custom_config\")\n    print(\"   example_custom_config()\")\n